mapper.select:
SELECT COUNT(*)
FROM review r
JOIN joinclass j ON r.join_class_id = j.join_class_id
WHERE r.join_class_id = #{joinClassId}
AND j.user_id = #{userId};

SELECT COUNT(*)
FROM joinclass
WHERE join_class_id = #{joinClassId};

SELECT COUNT(*) > 0
FROM academy
WHERE aca_id = #{acaId}

SELECT COUNT(*)
FROM joinclass
WHERE join_class_id = #{joinClassId}
AND certification = 1;

mapper.update:
UPDATE review r
INNER JOIN joinclass j ON r.join_class_id = j.join_class_id
SET r.comment = #{comment}, r.star = #{star}
WHERE r.join_class_id = #{joinClassId}
AND j.user_id = #{userId};

mapper.delete:
DELETE r
FROM review r
INNER JOIN joinclass j
ON r.join_class_id = j.join_class_id
WHERE r.join_class_id = #{joinClassId}
AND j.user_id = #{userId};

mapper.select:
SELECT role_id
FROM user
WHERE user_id = #{userId};

SELECT COUNT(*)
FROM academy a
JOIN user u ON a.user_id = u.user_id
WHERE a.aca_id = #{acaId}
AND u.user_id = #{userId}
AND u.role_id = 3;

SELECT COUNT(*)
FROM review r
JOIN joinclass j ON r.join_class_id = j.join_class_id
WHERE r.join_class_id = #{joinClassId}
AND j.class_id IN (
SELECT class_id
FROM class
WHERE aca_id = #{acaId}
);

SELECT COUNT(*)
FROM academy
WHERE aca_id = #{acaId}
